import { useState, useEffect } from 'react';
import { Theme } from './types';
import { links, categories } from './data/links';
import ThemeToggle from './components/ThemeToggle';
import SearchBar from './components/SearchBar';
import LinksGrid from './components/LinksGrid';

function App() {
  const [theme, setTheme] = useState<Theme>('light');
  const [searchTerm, setSearchTerm] = useState('');

  // Load theme from localStorage on mount
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') as Theme;
    if (savedTheme) {
      setTheme(savedTheme);
    } else {
      // Check system preference
      const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
      setTheme(prefersDark ? 'dark' : 'light');
    }
  }, []);

  // Apply theme to document
  useEffect(() => {
    document.documentElement.classList.toggle('dark', theme === 'dark');
    localStorage.setItem('theme', theme);
  }, [theme]);

  const handleThemeChange = (newTheme: Theme) => {
    setTheme(newTheme);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 to-blue-400 text-white transition-colors duration-300 relative overflow-hidden">
      <ThemeToggle theme={theme} onThemeChange={handleThemeChange} />
      <div className="container mx-auto px-4 py-8 max-w-7xl relative z-10">
        {/* Header */}
        <header className="text-center mb-12">
          <img src="https://andhrauniversity.edu.in/img/logo.png" alt="Andhra University Logo" className="mx-auto mb-4 w-24 h-24 rounded-full bg-white p-2 shadow-lg" />
          <h1 className="text-4xl md:text-6xl lg:text-7xl font-bold mb-2 tracking-tight">Andhra University</h1>
          <h2 className="text-xl md:text-2xl font-semibold mb-4 tracking-wide text-blue-100">Campus Companion</h2>
          <p className="text-lg md:text-xl max-w-3xl mx-auto leading-relaxed text-blue-100">
            Your gateway to all essential AU student resources and services.<br />
            NAAC A++ | NIRF 2024: 7th (State), 25th (University), 41st (Overall)
          </p>
        </header>

        {/* Search Bar */}
        <SearchBar searchTerm={searchTerm} onSearchChange={setSearchTerm} />

        {/* Links Grid */}
        <LinksGrid 
          links={links} 
          categories={categories} 
          searchTerm={searchTerm} 
        />

        {/* Footer */}
        <footer className="mt-20 pt-8 border-t border-blue-200/20 text-center">
          <p className="text-blue-100 text-sm mb-2">
            Â© {new Date().getFullYear()} Andhra University Campus Companion. Built for AU students.
          </p>
          <p className="text-blue-200 text-xs">
            Andhra University, Visakhapatnam, Andhra Pradesh, India &bull; Celebrating 99+ years of academic excellence
          </p>
        </footer>
      </div>
    </div>
  );
}

export default App;
