sourcesContent:
import { LinkItem, Category } from '../types';

export const categories: Category[] = [
  {
    id: 'academics',
    name: 'Academics',
    color: 'bg-blue-500',
    icon: 'BookOpen'
  },
  {
    id: 'finance',
    name: 'Finance',
    color: 'bg-green-500',
    icon: 'CreditCard'
  },
  {
    id: 'resources',
    name: 'Campus Resources',
    color: 'bg-purple-500',
    icon: 'Building'
  },
  {
    id: 'student-life',
    name: 'Student Life',
    color: 'bg-orange-500',
    icon: 'Users'
  }
];

export const links: LinkItem[] = [
  // Academics
  {
    id: 'academic-calendar',
    title: 'Academic Calendar',
    url: 'https://university.example.edu/academic-calendar',
    icon: 'Calendar',
    category: 'academics',
    description: 'Important dates and deadlines'
  },
  {
    id: 'examination-results',
    title: 'Examination Results',
    url: 'https://university.example.edu/results',
    icon: 'FileText',
    category: 'academics',
    description: 'Check your exam scores'
  },
  {
    id: 'course-catalog',
    title: 'Course Catalog',
    url: 'https://university.example.edu/courses',
    icon: 'BookOpen',
    category: 'academics',
    description: 'Browse available courses'
  },
  {
    id: 'library-portal',
    title: 'Library Portal',
    url: 'https://university.example.edu/library',
    icon: 'Library',
    category: 'academics',
    description: 'Access library resources'
  },
  
  // Finance
  {
    id: 'fee-payment',
    title: 'Fee Payment Portal',
    url: 'https://university.example.edu/payments',
    icon: 'CreditCard',
    category: 'finance',
    description: 'Pay tuition and fees'
  },
  {
    id: 'scholarship-info',
    title: 'Scholarship Information',
    url: 'https://university.example.edu/scholarships',
    icon: 'Trophy',
    category: 'finance',
