import { useState, useEffect } from 'react';
import { Theme } from './types';
import { links, categories } from './data/links';
import { announcements } from './data/announcements';
import ThemeToggle from './components/ThemeToggle';
import SearchBar from './components/SearchBar';
import AnnouncementsSection from './components/AnnouncementsSection';
import LinksGrid from './components/LinksGrid';

function App() {
  const [theme, setTheme] = useState<Theme>('light');
  const [searchTerm, setSearchTerm] = useState('');

  // Load theme from localStorage on mount
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') as Theme;
    if (savedTheme) {
      setTheme(savedTheme);
    } else {
      // Check system preference
      const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
      setTheme(prefersDark ? 'dark' : 'light');
    }
  }, []);

  // Apply theme to document
  useEffect(() => {
    document.documentElement.classList.toggle('dark', theme === 'dark');
    localStorage.setItem('theme', theme);
  }, [theme]);

  const handleThemeChange = (newTheme: Theme) => {
    setTheme(newTheme);
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 transition-colors duration-300">
      <ThemeToggle theme={theme} onThemeChange={handleThemeChange} />
      
      <div className="container mx-auto px-4 py-8 max-w-7xl">
        {/* Header */}
        <header className="text-center mb-8 animate-fade-in">
          <h1 className="text-4xl md:text-5xl font-bold text-gray-900 dark:text-white mb-4">
            Andhra University
          </h1>
          <h2 className="text-2xl md:text-3xl font-semibold text-primary-600 dark:text-primary-400 mb-4">
            Campus Companion
          </h2>
          <p className="text-lg text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
            Your comprehensive university dashboard with quick access to academic resources, 
            campus services, and student life information at Andhra University, Visakhapatnam.
          </p>
          <div className="mt-4 text-sm text-gray-500 dark:text-gray-400">
            Established in 1926 • NAAC A++ Accredited • UGC Recognized
          </div>
        </header>

        {/* Search Bar */}
        <SearchBar searchTerm={searchTerm} onSearchChange={setSearchTerm} />

        {/* Announcements Section */}
        <AnnouncementsSection announcements={announcements} />

        {/* Links Grid */}
        <LinksGrid 
          links={links} 
          categories={categories} 
          searchTerm={searchTerm} 
        />

        {/* Footer */}
        <footer className="mt-16 pt-8 border-t border-gray-200 dark:border-gray-700 text-center">
          <p className="text-gray-500 dark:text-gray-400 text-sm mb-2">
            © 2024 Andhra University Campus Companion. Built for AU students.
          </p>
          <p className="text-gray-400 dark:text-gray-500 text-xs">
            Andhra University, Visakhapatnam, Andhra Pradesh, India
          </p>
        </footer>
      </div>
    </div>
  );
}

export default App;
