import * as LucideIcons from 'lucide-react';
import { LinkItem } from '../types';

interface LinkCardProps {
  link: LinkItem;
  categoryColor: string;
}

const LinkCard: React.FC<LinkCardProps> = ({ link, categoryColor }) => {
  const IconComponent = (LucideIcons as any)[link.icon] || LucideIcons.Link;

  const handleClick = () => {
    window.open(link.url, '_blank', 'noopener,noreferrer');
  };

  return (
    <button
      onClick={handleClick}
      className="card p-6 text-left w-full group hover:shadow-xl transition-all duration-300"
    >
      <div className="flex items-start space-x-4">
        <div className={`p-3 rounded-xl ${categoryColor} text-white group-hover:scale-110 transition-transform duration-200 shadow-lg`}>
          <IconComponent className="w-6 h-6" />
        </div>
        <div className="flex-1 min-w-0">
          <h3 className="font-semibold text-gray-900 dark:text-white group-hover:text-primary-600 dark:group-hover:text-primary-400 transition-colors duration-200 font-body text-lg leading-tight">
            {link.title}
          </h3>
          {link.description && (
            <p className="text-sm text-gray-600 dark:text-gray-300 mt-2 font-body leading-relaxed">
              {link.description}
            </p>
          )}
        </div>
        <div className="opacity-0 group-hover:opacity-100 transition-opacity duration-200">
          <LucideIcons.ExternalLink className="w-4 h-4 text-gray-400" />
        </div>
      </div>
    </button>
  );
};

export default LinkCard;
