import { useState, useEffect } from 'react';
import { Theme } from './types';
import { links, categories } from './data/links';
import ThemeToggle from './components/ThemeToggle';
import SearchBar from './components/SearchBar';
import LinksGrid from './components/LinksGrid';

// Particle System Component
const ParticleSystem: React.FC = () => {
  useEffect(() => {
    const createParticle = () => {
      const particle = document.createElement('div');
      particle.className = 'particle';
      particle.style.left = Math.random() * 100 + '%';
      particle.style.animationDuration = (Math.random() * 3 + 3) + 's';
      particle.style.animationDelay = Math.random() * 2 + 's';
      document.body.appendChild(particle);
      
      setTimeout(() => {
        if (document.body.contains(particle)) {
          document.body.removeChild(particle);
        }
      }, 6000);
    };

    const interval = setInterval(createParticle, 300);
    return () => clearInterval(interval);
  }, []);

  return null;
};

function App() {
  const [theme, setTheme] = useState<Theme>('light');
  const [searchTerm, setSearchTerm] = useState('');

  // Load theme from localStorage on mount
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') as Theme;
    if (savedTheme) {
      setTheme(savedTheme);
    } else {
      // Check system preference
      const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
      setTheme(prefersDark ? 'dark' : 'light');
    }
  }, []);

  // Apply theme to document
  useEffect(() => {
    document.documentElement.classList.toggle('dark', theme === 'dark');
    localStorage.setItem('theme', theme);
  }, [theme]);

  const handleThemeChange = (newTheme: Theme) => {
    setTheme(newTheme);
  };

  return (
    <div className="min-h-screen transition-colors duration-300 relative overflow-hidden">
      <ParticleSystem />
      <ThemeToggle theme={theme} onThemeChange={handleThemeChange} />
      
      <div className="container mx-auto px-4 py-8 max-w-7xl relative z-10">
        {/* Header */}
        <header className="text-center mb-12 animate-fade-in">
          <h1 className="text-hero md:text-hero-md lg:text-hero-lg font-display font-bold mb-4 leading-tight holographic text-breathe neon-text">
            Andhra University
          </h1>
          <h2 className="text-2xl md:text-3xl font-display font-semibold cyberpunk text-float mb-6">
            Campus Companion
          </h2>
          <p className="text-lg md:text-xl font-body text-white max-w-3xl mx-auto leading-relaxed text-glow">
            Your comprehensive university dashboard with quick access to academic resources, 
            campus services, and student life information at Andhra University, Visakhapatnam.
          </p>
          <div className="mt-6 text-sm font-body tracking-wide animated-text text-float">
            Established in 1926 • NAAC A++ Accredited • UGC Recognized
          </div>
        </header>

        {/* Search Bar */}
        <SearchBar searchTerm={searchTerm} onSearchChange={setSearchTerm} />

        {/* Links Grid */}
        <LinksGrid 
          links={links} 
          categories={categories} 
          searchTerm={searchTerm} 
        />

        {/* Footer */}
        <footer className="mt-20 pt-8 border-t border-white/20 text-center">
          <p className="text-white/70 text-sm mb-2 font-body">
            © 2024 Andhra University Campus Companion. Built for AU students.
          </p>
          <p className="text-white/50 text-xs font-body">
            Andhra University, Visakhapatnam, Andhra Pradesh, India
          </p>
        </footer>
      </div>
    </div>
  );
}

export default App;
